function prevPage() {
    if (cPage == 1) {
        return 
    }
    document.getElementById("page-number").innerHTML = ""
    cPage -= 1
    loadPosts(0, cTopic, cPage)
    document.getElementById("page-number").innerHTML = prevPageStr+"Page "+cPage+nextPageStr
}

function nextPage() {
    document.getElementById("page-number").innerHTML = ""
    cPage += 1
    loadPosts(0, cTopic, cPage)
    document.getElementById("page-number").innerHTML = prevPageStr+"Page "+cPage+nextPageStr
}